import { HOST } from '@/env';
import {
  useCurrentDraggers,
  useDeleteDragger,
  useDraggers,
} from '@/remote/dragger';
import { Dragger } from '@/types/dragger';
import { DeleteOutlined, EditOutlined, EyeOutlined } from '@ant-design/icons';
import { Image, List, Typography } from 'antd';
import Link from 'next/link';
import styled from 'styled-components';
import { useApplicationInfo } from '../context/ApplicationInfoProvider';
import { AsyncButton } from '../gizmo/AsyncButton';

const FALL_BACK_URL =
  'data:image/png;base64,';
const DEFAULT_CATE = 'default';
const ListBox = styled.div`
  min-width: 600px;
  .ant-list-item-main {
    padding: 12px;
  }
`;

function renderCate(dragger: Dragger) {
  return (
    <>
      {dragger.category || DEFAULT_CATE}-{dragger.subcategory || DEFAULT_CATE}(
      {dragger.order || 0})
    </>
  );
}
export function DraggerList() {
  const app = useApplicationInfo();
  const { deleteDragger } = useDeleteDragger();
  const draggers = useCurrentDraggers();

  return (
    <ListBox>
      <Image.PreviewGroup>
        <List
          itemLayout="vertical"
          dataSource={draggers}
          renderItem={(dragger, index) => (
            <List.Item
              key={dragger.id}
              style={{
                display: 'flex',
                borderRadius: '0 0 8px 8px',
                border: '1px solid #f0f0f0',
                padding: 0,
              }}
              extra={
                <Image
                  src={dragger.img ? `${HOST}${dragger.img.url}` : undefined}
                  alt={`${dragger.label}的封面`}
                  placeholder
                  width={220}
                  height={220}
                  fallback={FALL_BACK_URL}
                  style={{
                    objectFit: 'scale-down',
                  }}
                />
              }
              actions={[
                <Link
                  passHref
                  legacyBehavior
                  href={{
                    pathname: '/app/[id]/components',
                    query: {
                      id: app.id,
                      search: dragger.component.name,
                    },
                  }}
                >
                  <Typography.Link>
                    <EyeOutlined />
                    查看组件
                  </Typography.Link>
                </Link>,
                // TODO: dragger编辑
                <Link passHref legacyBehavior href="#">
                  <Typography.Link>
                    <EditOutlined />
                    编辑导出信息
                  </Typography.Link>
                </Link>,
                <AsyncButton
                  icon={<DeleteOutlined />}
                  danger
                  onClick={() => deleteDragger(dragger.id)}
                  comfirm={{
                    title: `确定要删除导出[${dragger.label}]吗?`,
                  }}
                  type="text"
                >
                  删除导出
                </AsyncButton>,
              ]}
            >
              <List.Item.Meta
                title={
                  <Link
                    passHref
                    legacyBehavior
                    href={{
                      pathname: '/app/[id]/components',
                      query: {
                        id: app.id,
                        search: dragger.component.name,
                      },
                    }}
                  >
                    <Typography.Link>
                      <Typography.Title level={4}>
                        {dragger.label}
                      </Typography.Title>
                    </Typography.Link>
                  </Link>
                }
                description={renderCate(dragger)}
              />
            </List.Item>
          )}
        />
      </Image.PreviewGroup>
    </ListBox>
  );
}
